/**
 * Scarica questo file Chart.js per utilizzarlo come fallback locale
 * Salvalo in assets/js/chart.min.js
 * 
 * Puoi scaricarlo con questo comando dall'interno della cartella assets/js:
 * wget https://cdn.jsdelivr.net/npm/chart.js@3.7.0/dist/chart.min.js
 * 
 * In alternativa, puoi scaricarlo manualmente da questo link:
 * https://cdn.jsdelivr.net/npm/chart.js@3.7.0/dist/chart.min.js
 */

// Oppure crea un file vuoto e aggiungi questo script che caricher√† Chart.js a runtime:

// Chart.js Loader
(function() {
    // Create a script element
    var script = document.createElement('script');
    
    // Set the source to the Chart.js CDN
    script.src = 'https://cdn.jsdelivr.net/npm/chart.js@3.7.0/dist/chart.min.js';
    
    // Add error handling
    script.onerror = function() {
        console.error('Failed to load Chart.js from CDN');
        // Try another CDN as fallback
        var fallbackScript = document.createElement('script');
        fallbackScript.src = 'https://cdnjs.cloudflare.com/ajax/libs/Chart.js/3.7.0/chart.min.js';
        fallbackScript.onerror = function() {
            console.error('Failed to load Chart.js from fallback CDN');
        };
        document.head.appendChild(fallbackScript);
    };
    
    // Add it to the document
    document.head.appendChild(script);
})();